	(14858,503,'testimonials_6_testimonial_image','498'),
	(14859,503,'_testimonials_6_testimonial_image','field_56e2c559ae170'),
	(14860,503,'testimonials_6_testimonial_content','[:en]Your special touch for my milestone celebration of becoming a colonel of the United States was highly appreciated by all my guests . Thank you so much ! [:fr]Votre touche spéciale pour célébrer ma montée en grade de colonel des Etats-Unis a été fortement apprécié par tous mes invités. Merci Alejandra![:]'),
	(14861,503,'_testimonials_6_testimonial_content','field_56e2c567ae171'),
	(14862,503,'testimonials_6_testimonial_name','Colonel Schamell (France)'),
	(14863,503,'_testimonials_6_testimonial_name','field_56e2c581ae172'),
	(14864,503,'testimonials_6_event_category','a:1:{i:0;s:1:\"1\";}'),
