	(19425,678,'testimonials_0_testimonial_content','[:en]Alejandra is the graff of an event planning.[:fr]Elle est le graff des événements .[:]'),
	(19426,678,'_testimonials_0_testimonial_content','field_56e2c567ae171'),
	(19427,678,'testimonials_0_testimonial_name','Arihiro Nakamura (Japan)'),
	(19428,678,'_testimonials_0_testimonial_name','field_56e2c581ae172'),
