	(21659,703,'testimonials_0_testimonial_content','[:en]Alejandra is the graff of an event planning.[:fr]Elle est le graff des événements .[:]'),
	(21660,703,'_testimonials_0_testimonial_content','field_56e2c567ae171'),
	(21661,703,'testimonials_0_testimonial_name','Arihiro Nakamura (Japan)'),
	(21662,703,'_testimonials_0_testimonial_name','field_56e2c581ae172'),
