	(21705,703,'testimonials_6_testimonial_image','498'),
	(21706,703,'_testimonials_6_testimonial_image','field_56e2c559ae170'),
	(21707,703,'testimonials_6_testimonial_content','[:en]Your special touch for my milestone celebration of becoming a colonel of the United States was highly appreciated by all my guests . Thank you so much ! [:fr]Votre touche spéciale pour célébrer ma montée en grade de colonel des Etats-Unis a été fortement apprécié par tous mes invités. Merci Alejandra![:]'),
	(21708,703,'_testimonials_6_testimonial_content','field_56e2c567ae171'),
	(21709,703,'testimonials_6_testimonial_name','Colonel Schamell (France)'),
	(21710,703,'_testimonials_6_testimonial_name','field_56e2c581ae172'),
	(21711,703,'testimonials_6_event_category','a:1:{i:0;s:1:\"1\";}'),
